import React, { useState } from 'react';
import Title from '../../Components/Title/Title';
import MainContent from '../../Components/MainContent/MainContent';
import Container from '../../Components/Container/Container';
import ImageIllustrator from '../../Components/ImageIllustrator/ImageIllustrator';
import eventImage from '../../assets/images/evento.svg';
import { Input } from '../../Components/FormComponents/FormComponents';
import { Button } from '../../Components/FormComponents/FormComponents';
import TableE from './TableE/TableE';
import api from '../../Services/Service';

const EventosPage = () => {
    const [frmEdit, setFrmEdit] = useState(false);
    const [nome, setNome] = useState("");
    const [descricao, setDescricao] = useState("");
    const [tipoEvento, setTipoEvento] = useState("");
    const [data, setData] = useState("");
    const [eventos, setEventos] = useState([]);





    useEffect(() => {
        //chamar a api
        async function getListarEventos() {
            
            try {
                const promise = await api.get(
                    "/Evento"
                );
                setEventos(promise.data);
            } catch (error) {
                console.error("Erro : " + error);
                alert("Erro ao carregar os eventos");
            }
            
        }

        getListarTipos();
        console.log("A HOME FOI MONTADA!")
    }, []);



    async function handleUpdate() {
        alert("Atualizar")
    }

    async function handleSubimit() {
        return;
    }
    async function showUpdateForm() {
        return;
    }
    async function handleDelete() {
        return;
    }




    return (
        <MainContent>
            <section className="cadastro-evento-section">
                <Container>
                    <div className="cadastro-evento__box">
                        <Title
                            titleText={"Eventos"}
                        />


                        <ImageIllustrator
                            alterText={""}
                            imageRender={eventImage}
                        />

                        <form className="ftipo-evento" onClick={frmEdit ? handleUpdate : handleSubimit}>
                            {!frmEdit ?
                                (<>
                                    {/* ******************************Cadastrar****************************** */}


                                    {/* Nome */}
                                    <Input
                                        id={"id"}
                                        placeholder={"Nome"}
                                        name={"nome"}
                                        type={"text"}
                                        required={"required"}
                                        value={nome}
                                        manipulationFunction={
                                            (e) => {
                                                setNome(e.target.value)
                                            }
                                        }
                                    />
                                    {/* Descricao */}
                                    <Input
                                        id={"id"}
                                        placeholder={"Descricao"}
                                        name={"descricao"}
                                        type={"text"}
                                        required={"required"}
                                        value={descricao}
                                        manipulationFunction={
                                            (e) => {
                                                setDescricao(e.target.value)
                                            }
                                        }
                                    />

                                    {/* Tipo Evento */}
                                    <Input
                                        id={"id"}
                                        placeholder={"Tipo evento"}
                                        name={"tipo"}
                                        type={"select"}
                                        required={"required"}
                                        value={tipoEvento}
                                        manipulationFunction={
                                            (e) => {
                                                setTipoEvento(e.target.value)
                                            }
                                        }
                                    />


                                    {/* Data */}
                                    <Input
                                        id={"id"}
                                        placeholder={"dd/mm/aaaa"}
                                        name={"data"}
                                        type={"date"}
                                        required={"required"}
                                        value={data}
                                        manipulationFunction={
                                            (e) => {
                                                setData(e.target.value)
                                            }
                                        }
                                    />

                                    {/* Botao para cadastrar */}
                                    <Button
                                        type={"subimit"}
                                        name={"cadastrar"}
                                        id={"cadastrar"}
                                        textButton={"Cadastrar"}
                                    />



                                </>)


                                :


                                (<>

                                </>)}
                        </form>
                    </div>
                </Container>
            </section>




            <section className="lista-eventos-section">
                <Container>
                    <Title
                        titleText={"Lista de eventos"}
                        color='white'
                    />

                    <TableE
                        dados={eventos}
                        fnUpdate={showUpdateForm}
                        fnDelete={handleDelete}
                    />
                </Container>
            </section>


        </MainContent>
    );
};

export default EventosPage;
